import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
import * as i2 from "@angular/router";
export class ServicepersonService {
    constructor(httpP, router) {
        this.httpP = httpP;
        this.router = router;
        this.url = "https://localhost:44377/api/";
    }
    getAllPerson() {
        this.httpP.get(this.url + "person").toPromise().then(res => {
            this.people = res;
        });
        console.log(" ------- GET PERSON ---------");
    }
    async getPerson(id) {
        return this.httpP.get(this.url + "person/" + id).toPromise().then(res => {
            //this.personn = res[0] as Person;
            return res[0];
        });
    }
    addPerson(newPerson) {
        const headers = { 'content-type': 'application/json' };
        const body = JSON.stringify(newPerson);
        this.httpP.post(this.url + "person", body, { 'headers': headers }).toPromise().then(r => {
            this.router.navigate(['person']);
        });
    }
    editPerson(id, updatePerson) {
        const headers = { 'content-type': 'application/json' };
        const body = JSON.stringify(updatePerson);
        this.httpP.put(this.url + "person/" + id, body, { 'headers': headers }).toPromise();
    }
    deletePerson(id) {
        const headers = { 'content-type': 'application/json' };
        this.httpP.delete(this.url + "person/" + id).toPromise();
    }
}
